<?phpclass Afiliado extends CI_Controller {		public function __construct()    {        parent::__construct();        if (!$this->session->userdata('logged_in'))        {			redirect('login/'.$this->uri->uri_string(), 'refresh');        }        $this->load->helper('url');        $this->load->helper('html');        $this->load->model('Afiliados');        $this->afiliados = $this->Afiliados;    }    			public function jsummary() 	{		echo json_encode($this->afiliados->get_summary());	}	public function jdetail()	{		echo json_encode($this->afiliados->get_detail());	}	public function progreso()	{		$data['afiliados']=$this->afiliados->get_afiliados();		$data['electores']=$this->afiliados->get_electores();		$this->load->view('progreso',$data);	}	public function paging()	{			$this->load->library('pagination');		$config['base_url'] = 'http://mobile.sdipr.net/afiliado/paging/';		$config['total_rows'] = $this->afiliados->get_afiliados()->total;		$config['per_page'] = 100; 		$config["num_links"] = 10;		$this->pagination->initialize($config); 				$page = ($this->uri->segment(3)) ? $this->uri->segment(3) : 0;		$data['results'] = $this->afiliados->fetch_electores(intval($page), $config['per_page']);		$data['links'] = $this->pagination->create_links();		$data['total_rows']=$config['total_rows'];		 		$this->load->view('paging', $data);		}	    public function precinto()    {                $this->load->library('table');        $data['afiliados']=$this->afiliados->get_total_precinto();        $this->load->view('total',$data);    }}